const Initial_State = {
  hidden: null,
};

const cartRedux = (state = Initial_State, action) => {
  switch (action.type) {
    case "Toggle_Cart":
      return {
        ...state,
        hidden: !state.hidden,
      };

    default:
      return state;
  }
};

export default userRedux;
