import { takeEvery } from "redux-saga/effects";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils.js";

import { FetchCollectionSucess, FetchCollectionFailure } from "./shop.actions";

export function* fetchCollectionsStartAsync() {
  yield console.log("Fired");

  return (dispatch) => {
    const collectionRef = firestore.collection("collections");

    collectionRef
      .get()
      .then((snapshot) => {
        const collectionsMap = convertCollectionsSnapshotToMap(snapshot);
        dispatch(FetchCollectionSucess(collectionsMap));
      })
      .catch((error) => dispatch(FetchCollectionFailure(error.message)));
  };
}

export function* FetchCollectionStart() {
  yield takeEvery("Fetch_Collection_Start", fetchCollectionsStartAsync);
}
