import { createSelector } from "reselect";

const selectShop = (state) => state.shopdata;

const collection_id_map = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
};

export const selectShopData = createSelector(
  [selectShop],
  (shopdata) => shopdata.Shop_Data
);
