const Initial_State = {
  hidden: true,
  cart: [],
};

const cartRedux = (state = Initial_State, action) => {
  switch (action.type) {
    case "Toggle_Cart":
      return {
        ...state,
        hidden: !state.hidden,
      };
    case "Add_Item":
      return {
        cart: [...state],
      };

    default:
      return state;
  }
};

export default cartRedux;
