const Initial_State = {
  currentUser: null,
  errorMsg: undefined,
};

const userRedux = (state = Initial_State, action) => {
  switch (action.type) {
    case "Google_SignIn_Success":
    case "Email_SignIn_Success":
      return {
        ...state,
        currentUser: action.payload,
      };
    case "":
    case "":
      return {
        ...state,
        isFetching: false,
        errorMsg: action.payload,
      };

    default:
      return state;
  }
};

export default userRedux;
