import { takeLatest, put, call } from "redux-saga/effects";
import {
  googleProvider,
  auth,
  createUserDoc,
} from "../../firebase/firebase.utils";

import {
  GoogleSignInSucess,
  GoogleSignInFailure,
  EmailSignInFailure,
  EmailSignInSucess,
} from "./user-action";

export function* Google() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserDoc, user);
    const userSnap = yield userRef.get();
    yield put(GoogleSignInSucess({ id: userSnap.id, ...userSnap.data() }));
  } catch (error) {
    yield put(GoogleSignInFailure(error.message));
  }
}

export function* GoogleSignInStart() {
  yield takeLatest("Google_SignIn_Start", Google);
}

export function* Email({ payload: { email, password } }) {
  try {
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    yield getSnapshotFromUserAuth(user);
  } catch (error) {
    yield put(signInFailure(error));
  }
}

export function* EmailSignInStart() {
  yield takeLatest("Email_SignIn_Start", Email);
}
